def create_aff_array(file_name,awl):
	
	with open(file_name,"r", encoding="utf-8") as fa:  
		line = fa.readline()
		cnt = 1
		validaff = 0
		while line:
			arraix=[]
			if(cnt !=1):
				line = fa.readline()
				single_word_arr = line.split(" ")
				if(single_word_arr):
					if(len(single_word_arr) ==5):
						arraix.append(single_word_arr[2])
						arraix.append(single_word_arr[3])
						
						awl[single_word_arr[1]] = arraix
						validaff +=1
			cnt += 1
	return awl	

def aff_generator(affdl):
	affixer = ""
	for fffcx in affdl["affpath"]:
		if(fffcx !=""):
			affixer = affixer+fffcx;
		else:
			break
	return affixer

